import React, { useState } from "react";
import Todo from "./Todo";
import FilterButton from "./FilterButton";
import { routes }  from "./routes";

export const FILTER_MAP = {
    All: () => true,
    Active: task => !task.completed,
    Completed: task => task.completed
  };

export const FILTER_NAMES = Object.keys(FILTER_MAP);
export const [tasks, setTasks] = useState(props.tasks);
export const [filter, setFilter] = useState('All');

export const taskList = tasks
.filter(FILTER_MAP[filter])
.map(task => (
<Todo
  id={task.id}
  name={task.name}
  completed={task.completed}
  key={task.id}
  toggleTaskCompleted={toggleTaskCompleted}
  deleteTask={deleteTask}
  editTask={editTask}
/>
));

export const filterList = FILTER_NAMES.map(name => (
    <FilterButton
      key={name}
      name={name}
      isPressed={name === filter}
      setFilter={setFilter}
    />
  ));

export function addTask(name) {
    const newTask = { id: "todo-" + nanoid(), name: name, completed: false };
    setTasks([...tasks, newTask]);
  }

export function toggleTaskCompleted(id) {
    const updatedTasks = tasks.map(task => {
      // if this task has the same ID as the edited task
      if (id === task.id) {
        // use object spread to make a new object
        // whose `completed` prop has been inverted
        return {...task, completed: !task.completed}
      }
      return task;
    });
    setTasks(updatedTasks);
  }

export function deleteTask(id) {
    const remainingTasks = tasks.filter(task => id !== task.id);
    setTasks(remainingTasks);
  }

export function editTask(id, newName) {
    const editedTaskList = tasks.map(task => {
    // if this task has the same ID as the edited task
      if (id === task.id) {
        //
        return {...task, name: newName}
      }
      return task;
    });
    setTasks(editedTaskList);
}

export function ListFiltered(filterList){
    return(
        <div className="filters btn-group stack-exception">
        {filterList}
      </div>
    );
}

export function ListOfTasks(taskList){
  return(
    <div className="main">
      <ul
      role="list"
      className="todo-list stack-large stack-exception"
      aria-labelledby="list-heading"
      >
        {taskList}
      </ul>
        {routes}
      </div>
  );
}
